// src/components/Transactions/Transactions.jsx
import React, { useState, useEffect, useCallback } from 'react';
import '../styles/Transactions.css';
import axios from 'axios';

const Transactions = () => {
    const [month, setMonth] = useState(3); // Default is March
    const [transactions, setTransactions] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [page, setPage] = useState(1);
    const itemsPerPage = 10; // Define items per page

    const months = [
        { name: 'January', value: 1 }, { name: 'February', value: 2 }, { name: 'March', value: 3 },
        { name: 'April', value: 4 }, { name: 'May', value: 5 }, { name: 'June', value: 6 },
        { name: 'July', value: 7 }, { name: 'August', value: 8 }, { name: 'September', value: 9 },
        { name: 'October', value: 10 }, { name: 'November', value: 11 }, { name: 'December', value: 12 }
    ];

    const fetchTransactions = useCallback(async () => {
        const res = await axios.get(`http://localhost:5000/api/transactions?month=${month}&page=${page}&search=${searchTerm}`);
        setTransactions(res.data);
    }, [month, page, searchTerm]);

    useEffect(() => {
        fetchTransactions();
    }, [fetchTransactions]);

    const handleMonthChange = (e) => {
        setMonth(parseInt(e.target.value));
        setPage(1); // Reset to first page on month change
    };

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    };

    // Calculate total items displayed on current page
    const totalItemsDisplayed = transactions.length;

    return (
        <div className="container">
            <div className="header">
                <input
                    type="text"
                    placeholder="Search transaction"
                    value={searchTerm}
                    onChange={handleSearchChange}
                />
                <select value={month} onChange={handleMonthChange}>
                    {months.map((m) => (
                        <option key={m.value} value={m.value}>{m.name}</option>
                    ))}
                </select>
            </div>

            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Title</th>
                        <th>Description</th>
                        <th>Price</th>
                        <th>Category</th>
                        <th>Sold</th>
                        <th>Image</th>
                    </tr>
                </thead>
                <tbody>
                    {transactions.map((t) => (
                        <tr key={t.id}>
                            <td>{t.id}</td>
                            <td>{t.title}</td>
                            <td>{t.description}</td>
                            <td>{t.price}</td>
                            <td>{t.category}</td>
                            <td>{t.sold ? 'Yes' : 'No'}</td>
                            <td><img src={t.image} alt={t.title} style={{ width: '50px' }} /></td>
                        </tr>
                    ))}
                </tbody>
            </table>

           
            <div className="pagination">
            <span>Page No: {page} </span>
                <span>Per Page: {totalItemsDisplayed} - {itemsPerPage}</span>
            </div>

            <div className="pagination">
                <button onClick={() => setPage(page - 1)} disabled={page === 1}>Previous</button>
                
                <button onClick={() => setPage(page + 1)} disabled={totalItemsDisplayed < itemsPerPage}>Next</button>
            </div>
        </div>
    );
};

export default Transactions;
