import React, { useState } from 'react';
import BarChart from './components/BarChart';
import Statistics from './components/Statistics';
import Transactions from './components/Transactions';

const App = () => {
    const [selectedMonth, setSelectedMonth] = useState('2022-03'); // Default to March

    const handleMonthChange = (e) => {
        const month = parseInt(e.target.value, 10);
        setSelectedMonth(`2022-${month.toString().padStart(2, '0')}`);
    };

    return (
        <div className="app">
            <header>
                <h1>Transaction Dashboard</h1>
                <select value={selectedMonth.split('-')[1]} onChange={handleMonthChange}>
                    {Array.from({ length: 12 }, (_, i) => (
                        <option key={i + 1} value={i + 1}>
                            {new Date(0, i).toLocaleString('en-US', { month: 'long' })}
                        </option>
                    ))}
                </select>
            </header>
            <Statistics selectedMonth={selectedMonth} />
            <BarChart selectedMonth={selectedMonth} />
            <Transactions selectedMonth={selectedMonth} />
        </div>
    );
};

export default App;
