import React, { useState, useEffect } from 'react';
import '../styles/BarChart.css';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import 'chart.js/auto';

const BarChart = ({ month }) => {
    // const [month, setMonth] = useState(3); 
    const [barChartData, setBarChartData] = useState(null);
    const priceRanges = ['0-100', '101-200', '201-300', '301-400', '401-500', '501-600', '601-700', '701-800', '801-900', '901-above'];
    const monthNames = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];

    useEffect(() => {
        const fetchBarChartData = async () => {
            try {
                const res = await axios.get(`http://localhost:5000/api/barchart?month=${month}`);
                const apiData = res.data;

                const mappedData = priceRanges.map(range => {
                    const foundRange = apiData.find(item => item.priceRange === range);
                    return foundRange ? foundRange.itemCount : 0;
                });

                const chartData = {
                    labels: priceRanges,
                    datasets: [
                        {
                            label: 'Items Count',
                            data: mappedData,
                            backgroundColor: 'rgba(75, 192, 192, 0.6)',
                            borderColor: 'rgba(75, 192, 192, 1)',
                            borderWidth: 1
                        }
                    ]
                };

                setBarChartData(chartData);
            } catch (error) {
                console.error('Error fetching bar chart data:', error);
            }
        };

        fetchBarChartData();
    }, [month]);

    const handleMonthChange = (e) => {
        setMonth(parseInt(e.target.value));
        setPage(1); // Reset to first page on month change
    };

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    };

    return (
        <div className="bar-chart">
             <div className="header">
                <input type="text" placeholder="Search transaction" value={searchTerm} onChange={handleSearchChange} />
                <select value={month} onChange={handleMonthChange}>
                    {months.map((m) => (
                        <option key={m.value} value={m.value}>{m.name}</option>
                    ))}
                </select>
            </div>
            <div className="chart-title">Bar Chart Stats - {monthNames[month - 1]}</div>
            <div className="bar-chart-container">
                {barChartData ? (
                    <Bar 
                        data={barChartData}
                        options={{
                            scales: {
                                y: {
                                    beginAtZero: true,
                                    ticks: {
                                        stepSize: 1, // Ensures whole numbers on y-axis
                                    }
                                },
                                x: {
                                    reverse: false,
                                }
                            }
                        }}
                    />
                ) : (
                    <p>Loading chart data...</p>
                )}
            </div>
        </div>
    );
};

export default BarChart;
