import React, { useState, useEffect } from 'react';
import '../styles/BarChart.css';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import 'chart.js/auto';

const BarChart = () => {
    const [month, setMonth] = useState(3); // Default to March
    const [barChartData, setBarChartData] = useState(null);

    const priceRanges = [
        '0-100', '101-200', '201-300', '301-400',
        '401-500', '501-600', '601-700', '701-800',
        '801-900', '901-above'
    ];

    const monthNames = [
        "January", "February", "March", "April",
        "May", "June", "July", "August",
        "September", "October", "November", "December"
    ];

    useEffect(() => {
        const fetchBarChartData = async () => {
            try {
                const res = await axios.get(`http://localhost:5001/api/barchart?month=${month}`);
                const apiData = res.data;

                const mappedData = priceRanges.map(range => {
                    const foundRange = apiData.find(item => item._id === range);
                    return foundRange ? foundRange.itemCount : 0;
                });

                const chartData = {
                    labels: priceRanges,
                    datasets: [
                        {
                            label: 'Items Count',
                            data: mappedData,
                            backgroundColor: '#3e7f91',
                            borderColor: '#3e7f91',
                            borderWidth: 1,
                        }
                    ]
                };

                setBarChartData(chartData);
            } catch (error) {
                console.error('Error fetching bar chart data:', error);
            }
        };

        fetchBarChartData();
    }, [month]);

    const handleMonthChange = (e) => {
        setMonth(parseInt(e.target.value));
    };

    return (
        <div className="bar-chart">
            <div className="header">
                <h1 className="chart-title">Bar Chart - {monthNames[month - 1]}</h1>
                <select value={month} onChange={handleMonthChange}>
                    {monthNames.map((name, index) => (
                        <option key={index} value={index + 1}>{name}</option>
                    ))}
                </select>
            </div>
            <div className="bar-chart-container">
                {barChartData ? (
                    <Bar
                        data={barChartData}
                        options={{
                            scales: {
                                y: {
                                    beginAtZero: true,
                                    ticks: {
                                        stepSize: 1,
                                    }
                                }
                            }
                        }}
                    />
                ) : (
                    <p>Loading chart data...</p>
                )}
            </div>
        </div>
    );
};

export default BarChart;
