import React, { useState, useEffect } from 'react';
import '../styles/Transactions.css';
import axios from 'axios';

const Transactions = ({ selectedMonth }) => {
    const [transactions, setTransactions] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [page, setPage] = useState(1);
    const itemsPerPage = 10;

    useEffect(() => {
        const fetchTransactions = async () => {
            try {
                const res = await axios.get(`http://localhost:5001/api/transactions?month=${selectedMonth}&page=${page}&search=${searchTerm}`);
                setTransactions(res.data);
            } catch (error) {
                console.error('Error fetching transactions data:', error);
            }
        };

        fetchTransactions();
    }, [selectedMonth, page, searchTerm]);

    return (
        <div className="transactions">
            <h2>Transaction Dashboard</h2>
            <div className="filters">
                <input
                    type="text"
                    placeholder="Search transaction"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
            </div>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Title</th>
                        <th>Description</th>
                        <th>Price</th>
                        <th>Category</th>
                        <th>Sold</th>
                        <th>Image</th>
                    </tr>
                </thead>
                <tbody>
                    {transactions.map((t) => (
                        <tr key={t.id}>
                            <td>{t.id}</td>
                            <td>{t.title}</td>
                            <td>{t.description}</td>
                            <td>{t.price}</td>
                            <td>{t.category}</td>
                            <td>{t.sold ? 'Yes' : 'No'}</td>
                            <td>
                                <img src={t.image} alt={t.title} style={{ width: '50px' }} />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="pagination">
                <button onClick={() => setPage(page - 1)} disabled={page === 1}>
                    Previous
                </button>
                <span>
                    Page {page}
                </span>
                <button onClick={() => setPage(page + 1)} disabled={transactions.length < itemsPerPage}>
                    Next
                </button>
            </div>
        </div>
    );
};

export default Transactions;
