import React, { useState, useEffect } from 'react';
import '../styles/Statistics.css';
import axios from 'axios';

const Statistics = () => {
    const [month, setMonth] = useState(3); // Default to March
    const [statistics, setStatistics] = useState({});

    const monthNames = [
        "January", "February", "March", "April",
        "May", "June", "July", "August",
        "September", "October", "November", "December"
    ];

    useEffect(() => {
        const fetchStatistics = async () => {
            try {
                const res = await axios.get(`http://localhost:5001/api/statistics?month=${month}`);
                setStatistics(res.data);
            } catch (error) {
                console.error('Error fetching statistics:', error);
            }
        };

        fetchStatistics();
    }, [month]);

    const handleMonthChange = (e) => {
        setMonth(parseInt(e.target.value));
    };

    return (
        <div className="statistics">
            <div className="header">
                <h1 className="stat-title">Statistics - {monthNames[month - 1]}</h1>
                <select value={month} onChange={handleMonthChange}>
                    {monthNames.map((name, index) => (
                        <option key={index} value={index + 1}>{name}</option>
                    ))}
                </select>
            </div>
            <div className="stat-box">
                <div className="stat-item">Total Sale: {statistics.totalSale}</div>
                <div className="stat-item">Total Sold Items: {statistics.totalSold}</div>
                <div className="stat-item">Total Not Sold Items: {statistics.totalNotSold}</div>
            </div>
        </div>
    );
};

export default Statistics;
