import React, { useState, useEffect } from 'react';
import '../styles/Statistics.css';
import axios from 'axios';

const Statistics = ({ selectedMonth }) => {
    const [statistics, setStatistics] = useState(null);

    useEffect(() => {
        const fetchStatistics = async () => {
            try {
                const res = await axios.get(`http://localhost:5001/api/statistics?month=${selectedMonth}`);
                setStatistics(res.data);
            } catch (error) {
                console.error('Error fetching statistics data:', error);
            }
        };

        fetchStatistics();
    }, [selectedMonth]);

    return (
        <div className="statistics">
            <h2>Statistics - {new Date(selectedMonth).toLocaleString('en-US', { month: 'long' })}</h2>
            {statistics ? (
                <div className="stat-box">
                    <div>Total Sale: {statistics.totalSale}</div>
                    <div>Total Sold Items: {statistics.totalSold}</div>
                    <div>Total Not Sold Items: {statistics.totalNotSold}</div>
                </div>
            ) : (
                <p>Loading statistics...</p>
            )}
        </div>
    );
};

export default Statistics;
