// src/App.js
import React from 'react';
import Transactions from './components/Transactions';

import Statistics from '../components/Statistics';
import BarChart from '../components/BarChart';

import Sidebar from './components/Sidebar';
import './App.css';

function App() {
    return (
      <Router>
      <div className="app-container">
        <header className="app-header">
          <h1>Student and Subject Management</h1>
        </header>
        <div className="app-body">
          <Sidebar />
          <div className="main-content">
            <Routes>
              <Route path="/subject-form" element={<Transactions />} />
              <Route path="/subject-list" element={<Statistics />} />
              <Route path="/student-form" element={<BarChart />} />

              {/* Default Route */}
              <Route path="/" element={<Transactions />} /> {/* Redirect to Subject List by default */}
            </Routes>
          </div>
        </div>
      </div>
    </Router>
    );
}

export default App;
