import React, { useState, useEffect, useCallback } from 'react';
import './styles.css';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import 'chart.js/auto';

const Transactions = () => {
    const [month, setMonth] = useState(3); // Default is March
    const [transactions, setTransactions] = useState([]);
    const [statistics, setStatistics] = useState({});
    const [barChartData, setBarChartData] = useState(null); // Initially set to null
    const [searchTerm, setSearchTerm] = useState('');
    const [page, setPage] = useState(1);
    
    const months = [
        { name: 'January', value: 1 }, { name: 'February', value: 2 }, { name: 'March', value: 3 },
        { name: 'April', value: 4 }, { name: 'May', value: 5 }, { name: 'June', value: 6 },
        { name: 'July', value: 7 }, { name: 'August', value: 8 }, { name: 'September', value: 9 },
        { name: 'October', value: 10 }, { name: 'November', value: 11 }, { name: 'December', value: 12 }
    ];

    // Use useCallback to ensure stable functions for useEffect dependencies
    const fetchTransactions = useCallback(async () => {
        const res = await axios.get(`http://localhost:5000/api/transactions?month=${month}&page=${page}&search=${searchTerm}`);
        setTransactions(res.data);
    }, [month, page, searchTerm]);

    const fetchStatistics = useCallback(async () => {
        const res = await axios.get(`http://localhost:5000/api/statistics?month=${month}`);
        setStatistics(res.data);
    }, [month]);

    const fetchBarChartData = useCallback(async () => {
        const res = await axios.get(`http://localhost:5000/api/barchart?month=${month}`);
        const priceRanges = res.data.map((item) => item.priceRange);
        const itemCounts = res.data.map((item) => item.itemCount);

        setBarChartData({
            labels: priceRanges,
            datasets: [{
                label: 'Items Count',
                data: itemCounts,
                backgroundColor: '#8dd3ff'
            }]
        });
    }, [month]);

    useEffect(() => {
        fetchTransactions();
        fetchStatistics();
        fetchBarChartData();
    }, [fetchTransactions, fetchStatistics, fetchBarChartData]);

    const handleMonthChange = (e) => {
        setMonth(parseInt(e.target.value));
        setPage(1); // Reset to first page on month change
    };

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    };

    return (
        <div className="container">
            <div className="header">
                <input type="text" placeholder="Search transaction" value={searchTerm} onChange={handleSearchChange} />
                <select value={month} onChange={handleMonthChange}>
                    {months.map((m) => (
                        <option key={m.value} value={m.value}>{m.name}</option>
                    ))}
                </select>
            </div>

            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Title</th>
                        <th>Description</th>
                        <th>Price</th>
                        <th>Category</th>
                        <th>Sold</th>
                        <th>Image</th>
                    </tr>
                </thead>
                <tbody>
                    {transactions.map((t) => (
                        <tr key={t.id}>
                            <td>{t.id}</td>
                            <td>{t.title}</td>
                            <td>{t.description}</td>
                            <td>{t.price}</td>
                            <td>{t.category}</td>
                            <td>{t.sold ? 'Yes' : 'No'}</td>
                            <td><img src={t.image} alt={t.title} style={{ width: '50px' }} /></td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <div className="pagination">
                <button onClick={() => setPage(page - 1)} disabled={page === 1}>Previous</button>
                <span>Page: {page}</span>
                <button onClick={() => setPage(page + 1)}>Next</button>
            </div>

            <div className="statistics">
                <div className="stat-box">
                    <div className="stat-item">Total Sale: {statistics.totalSale}</div>
                    <div className="stat-item">Total Sold Items: {statistics.totalSold}</div>
                    <div className="stat-item">Total Not Sold Items: {statistics.totalNotSold}</div>
                </div>
            </div>

            <div className="bar-chart">
    <div className="chart-title">Bar Chart Stats - {months.find((m) => m.value === month).name}</div>
    <div className="bar-chart-container">
        {barChartData ? (
            <Bar 
                data={barChartData}
                options={{
                    scales: {
                        y: {
                            beginAtZero: true,
                            ticks: {
                                stepSize: 1, // This ensures the y-axis shows whole numbers
                            }
                        },
                        x: {
                            reverse: true // This will reverse the bar chart to display from right to left
                        }
                    }
                }}
            />
        ) : (
            <p>Loading chart data...</p>
        )}
    </div>
</div>

        </div>
    );
};

export default Transactions;
