import React, { useState, useEffect, useCallback } from 'react';
import './styles.css';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import 'chart.js/auto';

const Transactions = () => {
    const [month, setMonth] = useState(3); // Default is March
    const [transactions, setTransactions] = useState([]);
    const [statistics, setStatistics] = useState({});
    const [barChartData, setBarChartData] = useState(null); // Initially set to null
    const [searchTerm, setSearchTerm] = useState('');
    const [page, setPage] = useState(1);
    const itemsPerPage = 10; // Max items per page

    const monthNames = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    
    const months = [
        { name: 'January', value: 1 }, { name: 'February', value: 2 }, { name: 'March', value: 3 },
        { name: 'April', value: 4 }, { name: 'May', value: 5 }, { name: 'June', value: 6 },
        { name: 'July', value: 7 }, { name: 'August', value: 8 }, { name: 'September', value: 9 },
        { name: 'October', value: 10 }, { name: 'November', value: 11 }, { name: 'December', value: 12 }
    ];

    // Define the consistent price ranges
    const priceRanges = ['0-100', '101-200', '201-300', '301-400', '401-500', '501-600', '601-700', '701-800', '801-900', '901-above'];

    // Use useCallback to ensure stable functions for useEffect dependencies
    const fetchTransactions = useCallback(async () => {
        const res = await axios.get(`http://localhost:5000/api/transactions?month=${month}&page=${page}&search=${searchTerm}&limit=${itemsPerPage}`);
        setTransactions(res.data.transactions); // Assume response contains transactions array
    }, [month, page, searchTerm]);

    const fetchStatistics = useCallback(async () => {
        const res = await axios.get(`http://localhost:5000/api/statistics?month=${month}`);
        setStatistics(res.data);
    }, [month]);

    const fetchBarChartData = async () => {
        try {
            const res = await axios.get(`http://localhost:5000/api/barchart?month=${month}`);
            const apiData = res.data;

            // Map API data to the price ranges
            const mappedData = priceRanges.map(range => {
                const foundRange = apiData.find(item => item.priceRange === range);
                return foundRange ? foundRange.itemCount : 0; // Return count or 0 if not found
            });

            // Prepare data for the bar chart
            const chartData = {
                labels: priceRanges,
                datasets: [
                    {
                        label: 'Items Count',
                        data: mappedData, // Use the mapped data
                        backgroundColor: 'rgba(75, 192, 192, 0.6)',
                        borderColor: 'rgba(75, 192, 192, 1)',
                        borderWidth: 1
                    }
                ]
            };

            setBarChartData(chartData);
        } catch (error) {
            console.error('Error fetching bar chart data:', error);
        }
    };

    useEffect(() => {
        fetchTransactions();
        fetchStatistics();
        fetchBarChartData();
    }, [fetchTransactions, fetchStatistics, fetchBarChartData]);

    const handleMonthChange = (e) => {
        setMonth(parseInt(e.target.value));
        setPage(1); // Reset to first page on month change
    };

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
        setPage(1); // Reset to first page on search
    };

    const handleNextPage = () => {
        setPage(prevPage => prevPage + 1);
    };

    const handlePreviousPage = () => {
        setPage(prevPage => prevPage - 1);
    };

    return (
        <div className="container">
            <div className="header">
                <input type="text" placeholder="Search transaction" value={searchTerm} onChange={handleSearchChange} />
                <select value={month} onChange={handleMonthChange}>
                    {months.map((m) => (
                        <option key={m.value} value={m.value}>{m.name}</option>
                    ))}
                </select>
            </div>

            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Title</th>
                        <th>Description</th>
                        <th>Price</th>
                        <th>Category</th>
                        <th>Sold</th>
                        <th>Image</th>
                    </tr>
                </thead>
                <tbody>
                    {transactions.length > 0 ? (
                        transactions.map((t) => (
                            <tr key={t.id}>
                                <td>{t.id}</td>
                                <td>{t.title}</td>
                                <td>{t.description}</td>
                                <td>{t.price}</td>
                                <td>{t.category}</td>
                                <td>{t.sold ? 'Yes' : 'No'}</td>
                                <td><img src={t.image} alt={t.title} style={{ width: '50px' }} /></td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="7">No transactions found.</td>
                        </tr>
                    )}
                </tbody>
            </table>

            <div className="pagination">
                <button onClick={handlePreviousPage} disabled={page === 1}>Previous</button>
                <span>Page: {page}</span>
                <button onClick={handleNextPage}>Next</button>
            </div>

            <div className="statistics">
                <div className="stat-box">
                    <div className="stat-item">Total Sale: {statistics.totalSale}</div>
                    <div className="stat-item">Total Sold Items: {statistics.totalSold}</div>
                    <div className="stat-item">Total Not Sold Items: {statistics.totalNotSold}</div>
                </div>
            </div>

            <div className="bar-chart">
                <div className="chart-title">Bar Chart Stats - {monthNames[month - 1]}</div>
                <div className="bar-chart-container">
                    {barChartData ? (
                        <Bar 
                            data={barChartData}
                            options={{
                                scales: {
                                    y: {
                                        beginAtZero: true,
                                        ticks: {
                                            stepSize: 1, // Ensures whole numbers on y-axis
                                        }
                                    },
                                    x: {
                                        reverse: false, // Ensure x-axis is not reversed
                                    }
                                }
                            }}
                        />
                    ) : (
                        <p>Loading chart data...</p>
                    )}
                </div>
            </div>

        </div>
    );
};

export default Transactions;
